    private final String clientAccountAuth;
    private final boolean subAccountFlg;
    private final String clientAccountAuthExt;

    ClientAuthorityDiv(int clientAccountAuthId, String clientAccountAuthName, String clientAccountAuth, boolean subAccountFlg, String clientAccountAuthExt) {
		this.clientAccountAuthId = clientAccountAuthId;
		this.clientAccountAuthName = clientAccountAuthName;
		this.clientAccountAuth = clientAccountAuth;
		this.subAccountFlg = subAccountFlg;
		this.clientAccountAuthExt = clientAccountAuthExt;
	    }

    public int getClientAccountAuthId() {
    	return clientAccountAuthId;
    }

    public String getClientAccountAuthName() {
    	return clientAccountAuthName;
    }

    public String getClientAccountAuth() {
    	return clientAccountAuth;
    }

    public boolean getSubAccountFlg() {
    	return subAccountFlg;
    }

    public String getClientAccountAuthExt() {
    	return clientAccountAuthExt;
    }

    public static ClientAuthorityDiv get(String codeStr) {
		Integer code = NumberUtil.toInteger(codeStr);
		return (code != null) ? get(code.intValue()) : null;
    }

    public static ClientAuthorityDiv get(Integer clientAuthId) {
    	if (clientAuthId == null) {
    		return null;
    	}
		for (ClientAuthorityDiv value : values()) {
		    if (value.clientAccountAuthId == clientAuthId) {
			return value;
		    }
		}
		return null;
    }

    /** [開発者向け説明] JSPから取るための配列 */
    public static final ClientAuthorityDiv[] ITEMS = values();

